import React, {FC} from 'react';
import styles from "./Users.module.css";
import avatar from "../../assets/images/avatar.png";
import {unfollowSuccess, UsersData} from "../../redux/usersReducer";
import {NavLink} from "react-router-dom";
import {Paginator} from "../common/Paginator/Paginator";

export type PropsUserType = {
  usersPage: UsersData[]
  pageSize: number
  totalUsersCount: number
  currentPage: number
  follow: (userId: number) => void
  unfollow: (userId: number) => void
  setUsers: (users: any) => void
  setCurrentPage: (currentPage: number) => void
  setTotalCount: (totalUsersCount: number) => void
  onPageChanged: (pageNumber: number) => void
  followingInProgress: Array<any>
}

const Users: FC<PropsUserType> = ({
                                    pageSize,
                                    onPageChanged,
                                    currentPage,
                                    usersPage,
                                    totalUsersCount,
                                    follow,
                                    followingInProgress,
                                    unfollow, ...props
                                  }) => {

  return (
    <div>
      <Paginator pageSize={pageSize} currentPage={currentPage} onPageChanged={onPageChanged}
                 totalUsersCount={totalUsersCount}/>
      USERS
      {usersPage.map(u =>
        <div key={u.id}>
          <NavLink to={'/profile/' + u.id}>
            <img src={u.photos.small != null ? u.photos.large : avatar} alt="picture"
                 className={styles.avatar}/>
          </NavLink>


          <div>
            {u.followed
              ? <button disabled={followingInProgress.some(id => id === u.id)} onClick={() => {
                unfollow(u.id)
              }}>Unfollow</button>
              : <button disabled={followingInProgress.some(id => id === u.id)} onClick={() => {
                follow(u.id)
              }}>Follow</button>
            }
          </div>
          <div>
            {u.name}
          </div>
        </div>
      )
      }
    </div>
  );
};

export default Users;
