import {Dispatch} from "redux";
import {authAPI} from "../api/api";
import {AppDispatch, DispatchType} from "./redux-store";
import {stopSubmit} from "redux-form";
import {Simulate} from "react-dom/test-utils";

export type authReducersActionType =
    | ReturnType<typeof setUserData>

const SET_USER_DATA = "SET_USER_DATA";

export type UserDataType = {
    id: string | null,
    email: string | null,
    login: string | null
    isAuth: boolean
}

const initialState = {
    id: null,
    email: null,
    login: null,
    isAuth: false
}


export const authReducer = (state: UserDataType = initialState, action: authReducersActionType): UserDataType => {
    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.payload
            }
        default:
            return state;
    }
};

export const setUserData = (id: string | null, email: string | null, login: string | null, isAuth: boolean) => ({
        type: SET_USER_DATA,
        payload: {id, email, login, isAuth}
    } as const
)


export const getAuthUserData = () => {
    return (dispatch: Dispatch) => {
        authAPI.authMe()
            .then(response => {
                if (response.data.resultCode === 0) {
                    let {id, email, login} = response.data.data;
                    console.log(id, email, login)
                    dispatch(setUserData(id, email, login, true))
                }
            });
    }
}

export const login = (email: string, password: string, rememberMe: boolean, captcha: boolean) => {

    // let action = stopSubmit('email', {_error: 'error'})
    // console.log(action)
    return (dispatch: DispatchType) => {
        authAPI.login(email, password, rememberMe, captcha)
            .then(res => {
                if (res.data.resultCode === 0) {
                    dispatch(getAuthUserData())
                } else {
                    if(res.data.messages.length > 0){
                        dispatch(stopSubmit('email', {_error: res.data.messages}));
                    }
                }
            })
    }
}

export const logout = () => {
    return (dispatch: Dispatch) => {
        authAPI.logout()
            .then(res => {
                if (res.data.resultCode === 0) {
                    dispatch(setUserData(null, null, null, false))
                }
            })
    }
}
