import React from 'react';
import {ProfileType} from "../../../../redux/profileReducer";
import {Input} from "../../../common/formControls/formControl";
import {requiredField} from "../../../../utils/validators/validators";
import {Field, InjectedFormProps} from "redux-form";

type ProfileDataType = {
  profile: ProfileType
  status: string
  updateStatus: (status: string) => void
  callback: () => void
}


export const ProfileData = (props: ProfileDataType) => {
  return (
    <>
      <div>
        <button onClick={props.callback}>edit</button>
      </div>
      <div>
        <b>Full name:</b> {props.profile.fullName}
      </div>

      <div>
        <b>Looking for a job: </b> {props.profile.lookingForAJob ? "yes" : "no"}
      </div>

      {props.profile.lookingForAJob &&
          <div>
              <b>Description: </b>{props.profile.lookingForAJobDescription}
          </div>
      }
    </>
  );
};


type ProfileFormType = {
  profile: ProfileType
  status: string
  updateStatus: (status: string) => void
}
export const ProfileDataForm: React.FC<InjectedFormProps<ProfileFormType>> = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
      <div>
        <b>Full name:</b>
        <Field placeholder={'Full name'}
               name={'name'}
               component={Input}
               validate={[requiredField]}/>
      </div>

      <div>
        <b>Looking for a job: </b>
      </div>

      <div>
        <b>Description: </b>
      </div>

    </form>
  );
};
